import { useEffect, useState } from "react";
import { API_URL, COMMENT_ENDPOINT } from "../services/auth.constants";
import axios from "axios";

function ViewComments({ trailId }) {
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        const getReviews = async () => {
            let config = {
                url: COMMENT_ENDPOINT,
                baseURL: API_URL,
                method: "get",
            };
            let response = await axios.request(config);
            setReviews(response.data);
        };
        getReviews();
    }, [])

    const filterReviews = reviews.filter(review => review.trail === trailId)
    console.log(filterReviews)

    return (
        <div>
            Here are the comments!
            <div>
                {filterReviews.map((reviews) => <CommentCard key={reviews.id} review={reviews}/>)}
            </div>
        </div>
    )
}

export const CommentCard = ({ review }) => {
    return (
        <>
            <div>user</div>
            <div>Date Visited</div>
            <div>Rating {review.stars}</div>
            <div>Review: {review.text}</div>
        </>
    )
}

export default ViewComments;
