import { useState } from "react";
import { useGlobalState } from "../context/GlobalState";
import { Link } from "react-router-dom";

function MakeComment({ trail }) {

    const [state, dispatch] = useGlobalState();

    const [review, setReview] = useState({
        text: "",
        rating: "",
        dateVisited: "",
        user: state.currentUser?.email,
        trail: trail
    });

    const handleChange = (key, value) => {
        setReview({
            ...review,
            [key]: value
        })
    };

    const handleSubmitReview = (e) => {
        e.preventDefault();


        console.log("Review Submitted Maybe?")
        window.location.href = `/trail/${trail}`;
        window.location.reload(true);
    }


  return (
    <div>
      {!state.currentUser && (
        <Link to="/login" id="loginButton" className="nav-item btn me-5">
          Sign In To Leave Comment
        </Link>
      )}
      {state.currentUser && (
        <>
          <form onSubmit={handleSubmitReview}>
          <div>
            <label htmlFor="text">Review of Trail:</label>
            <input
              className="mb-3 ms-1"
              type="text"
              id="text"
              name="text"
              onChange={(e) => handleChange('text', e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="rating">Rating:</label>
            <input
              className="mb-3 me-4 ms-1"
              type="number"
              id="rating"
              name="rating"
              required
              onChange={(e) => handleChange('rating', e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="dateVisited">Date:</label>
            <input
              className="mb-3 me-4 ms-1"
              type="date"
              id="dateVisited"
              name="dateVisited"
              required
              onChange={(e) => handleChange('dateVisited', e.target.value)}
            />
          </div>
          <input
            type="submit"
            value="Submit Trail Review"
            id="SubmitBtn"
            className="btn btn-primary mb-5"
          />
        </form>
        </>
      )}
    </div>
  );
}

export default MakeComment;
