import { useEffect, useState } from "react";
import { API_URL, COMMENT_ENDPOINT } from "../services/auth.constants";
import axios from "axios";

function ViewComments({ trail }) {
    const [review, setReview] = useState([]);

    useEffect(() => {
        const getReview = async () => {
            let config = {
                url: COMMENT_ENDPOINT,
                baseURL: API_URL,
                method: "get",
            };
            let response = await axios.request(config);
            setReview(response.data);
        };
        getReview();
    }, [])

    const filterReview = (review) => {
        if (!review.trail) {
            return (
                <div>Be the first to review this trail</div>
            )
        }
        return review.filter((review) => {
            return review.trail
        })
    }

    return (
        <div>
            Here are the comments!
        </div>
    )
}

export const CommentCard = ({ review }) => {
    return (
        <>
            <div>user</div>
            <div>Date Visited</div>
            <div>Rating</div>
            <div>Review</div>
        </>
    )
}

export default ViewComments;
