import { useState } from "react";
import { useGlobalState } from "../context/GlobalState";
import axios from "axios";
import { API_URL } from "../services/auth.constants";
import { Link } from "react-router-dom";

function MakeComment({ trail }) {

    const [state, dispatch] = useGlobalState();

    const [review, setReview] = useState({
        text: "",
        rating: "",
        dateVisited: "",
        user: state.currentUser?.email,
        trail: trail
    });

    const [text, setText] = useState("");
    const [rating, setRating] = useState("");
    const [dateVisited, setDateVisited] = useState("");

    async function Submit() {
        if (rating < 0 || rating > 5) {
            alert("Please enter a rating between 0 and 5.");
            return;
        }
        await axios
            .post(API_URL + "comments/", {
                text: text,
                rating: rating,
                dateVisited: dateVisited,
                user: state.currentUser?.email,
                trail: trail
            })
            .then((resp) => {
                console.log(resp);
            });
        window.location.reload(true);
    }


  return (
    <div>
      {!state.currentUser && (
        <Link to="/login" id="loginButton" className="nav-item btn me-5">
          Sign In To Leave Comment
        </Link>
      )}
      {state.currentUser && (
        <>
          <form onSubmit={Submit}>
          <div>
            <label htmlFor="text">Review of Trail:</label>
            <input
              className="mb-3 ms-1"
              type="text"
              id="text"
              name="text"
              onChange={(e) => setText(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="pass">Rating:</label>
            <input
              className="mb-3 me-4 ms-1"
              type="number"
              id="rating"
              name="rating"
              required
              onChange={(e) => setRating(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="pass">Date:</label>
            <input
              className="mb-3 me-4 ms-1"
              type="date"
              id="date"
              name="date"
              required
              onChange={(e) => setDateVisited(e.target.value)}
            />
          </div>
          <input
            type="submit"
            value="Submit Trail Review"
            id="SubmitBtn"
            className="btn mb-5"
          />
        </form>
        </>
      )}
    </div>
  );
}

export default MakeComment;
