import { useState, useEffect } from "react";
import axios from 'axios';

const BASE_URL = 'https://8000-mkrs90-wildsteppebacken-vpz7io4uhwz.ws-us94.gitpod.io/api'

function TrailList() {
    const [trails, setTrails] = useState([]);

    useEffect(() => {
        const getTrails = async () => {
            let config = {
                url: '/trails/',
                baseURL: BASE_URL,
                method: 'get'
            }
            let response = await axios.request(config);
            setTrails(response.data);
        }
        getTrails();
    }, [])


    return (
        <div className="container mt-5">
            <div>Trails Near You</div>
            <div className="row justify-content-evenly">
                {trails.map(trail => {
                return <TrailCard trail={trail}/>
                })}
            </div>
        </div>
    )
}

export const TrailCard = ({ trail }) => {
    return (
        <>
            <div className="card" style={{width: '18rem'}}>
                <img src="..." className="card-img-top" alt="..." />
                    <div className="card-body">
                        <h5 className="card-title">{trail.name}</h5>
                        <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                        <a href="#" className="btn btn-primary">See the Trail</a>
                    </div>
            </div>
        </>
    )
}

export default TrailList;
