import { useState, useEffect } from "react";
import { useGlobalState } from "../context/GlobalState";
import { Link } from "react-router-dom";
import axios from "axios";
import { API_URL } from "../services/auth.constants";

function MakeComment({ trail }) {

    const [state, dispatch] = useGlobalState();
    const [text, setText] = useState("");
    const [rating, setRating] = useState("");
    const [dateVisited, setDateVisited] = useState("");
    const [email, setEmail] = useState("");

    const handleSubmitReview = (e) => {
        e.preventDefault();

        axios.post(API_URL + "comments/", {
            text: text,
            rating: rating,
            date: dateVisited,
            user_id: email,
            trail_id: trail 
        })
        .then((response) => {
            setText("");
            setRating("");
            setDateVisited("");
            console.log(response)
        })
        .catch((error) => {
            console.log(error);
        })
        console.log("Review Submitted Maybe?")
        window.location.href = `/trail/${trail}`;
        window.location.reload(true);
    }

    useEffect(() => {
        setEmail(state.currentUser?.email);
    }, [state]);

  return (
    <div>
      {!state.currentUser && (
        <Link to="/login" id="loginButton" className="nav-item btn me-5">
          Sign In To Leave Comment
        </Link>
      )}
      {state.currentUser && (
        <>
          <form onSubmit={handleSubmitReview}>
          <div>
            <label htmlFor="text">Review of Trail:</label>
            <input
              className="mb-3 ms-1"
              type="text"
              id="text"
              name="text"
              onChange={(e) => setText(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="rating">Rating:</label>
            <input
              className="mb-3 me-4 ms-1"
              type="number"
              min="1"
              max="5"
              id="rating"
              name="rating"
              required
              onChange={(e) => setRating(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="dateVisited">Date:</label>
            <input
              className="mb-3 me-4 ms-1"
              type="date"
              id="dateVisited"
              name="dateVisited"
              required
              onChange={(e) => setDateVisited(e.target.value)}
            />
          </div>
          <input
            type="submit"
            value="Submit Trail Review"
            id="SubmitBtn"
            className="btn btn-primary mb-5"
          />
        </form>
        </>
      )}
    </div>
  );
}

export default MakeComment;
