import { useState, useEffect, useRef } from "react";
import axios from "axios";
import { API_URL, COMMENT_ENDPOINT } from "../services/auth.constants";

function StarAverage({ trailId }) {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const getReviews = async () => {
      let config = {
        url: COMMENT_ENDPOINT,
        baseURL: API_URL,
        method: "get",
      };
      let response = await axios.request(config);
      setReviews(response.data);
    };
    getReviews();
  }, []);

  //   let currentTrailReviews = reviews.filter((r) => r.trail.id === trailId);
  //   console.log(currentTrailReviews)

  let filteredReviews = reviews.filter((r) => r.trail.id === trailId())
  return (
    <div>
      <div>
        <GetAverage key={reviews.id} reviews={reviews} />
      </div>
    </div>
  );
}

export default StarAverage;

const GetAverage = ({ reviews }) => {
  const [stars, setStars] = useState(0);

  let sum = 0;

  useEffect(() => {
    if (reviews.length > 0) {
      reviews?.forEach((review) => {
        console.log(review);
        sum += review.stars;
      });
      const average = sum / reviews.length;
      setStars(average);
    }
  }, [reviews]);

  console.log(stars);

  // console.log(stars);

  // console.log(avg);
  // console.log(starAvg);
  // const count = useRef(0);

  // useEffect(() => {
  //   count += {stars};
  // });

  // console.log(reviews.stars);

  // setStars(review.stars)
  // console.log(stars);

  // review.stars.forEach(item => {
  //   sum += item;
  // })

  // const star = review.stars

  const createStars = (sr) => {
    const stars = [];
    for (let i = 0; i < sr; i++) {
      stars.push(<i className="fa fa-solid fa-star" id="rating_star"></i>);
    }
    return stars;
  };

  return (
    <div className="row">
      <div id="rating_stars" className="ms-3">
        {createStars(stars)}
      </div>
    </div>
  );
};
