import { useEffect, useState } from "react";
import { API_URL, COMMENT_ENDPOINT } from "../services/auth.constants";
import axios from "axios";

function ViewComments({ trailId }) {
    const [review, setReview] = useState([]);

    useEffect(() => {
        const getReview = async () => {
            let config = {
                url: COMMENT_ENDPOINT,
                baseURL: API_URL,
                method: "get",
            };
            let response = await axios.request(config);
            setReview(response.data);
        };
        getReview();
    }, [])

    const filterReview = (reviews) => {
        if (!reviews.trail) {
            return (
                <div>Be the first to review this trail</div>
            )
        }
        return reviews.filter((reviews) => {
            return reviews.trail === trailId;
        });
    };

    return (
        <div>
            Here are the comments!
            <div>
                {filterReview(review).map((review) => <CommentCard key={review.id} review={review}/>)}
            </div>
        </div>
    )
}

export const CommentCard = ({ review }) => {
    return (
        <>
            <div>user</div>
            <div>Date Visited</div>
            <div>Rating {review.stars}</div>
            <div>Review: {review.text}</div>
        </>
    )
}

export default ViewComments;
