import { useState, useEffect } from "react";
import axios from "axios";
import { API_URL, COMMENT_ENDPOINT } from "../services/auth.constants";

function StarAverage({ trailId }) {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const getReviews = async () => {
      let config = {
        url: COMMENT_ENDPOINT,
        baseURL: API_URL,
        method: "get",
      };
      let response = await axios.request(config);
      setReviews(response.data);
    };
    getReviews();
  }, []);

  let currentTrailReviews = reviews.filter((r) => r.trail.id === trailId);


  return (
    <div>
      <div>
        {reviews
          .filter((r) => r.trail.id === trailId)
          .map((reviews) => (
            <GetAverage key={reviews.id} review={reviews} />
          ))}
      </div>
    </div>
  );
}

